def.body:
    def after

{% if @type.methods.find do |__arg0|
  __arg0.name == "after"
end %}
        previous_def
        {{ (yield).id }}
      {% else %}{% if @type.superclass && (@type.superclass.has_method?(:after)) %}
        super
        {{ (yield).id }}
      {% else %}
        {{ (yield).id }}
      {% end %}{% end %}


end

    def before

{% if existing = @type.methods.find do |d|
  d.name == "before"
end %}
        {% if @type.superclass && (@type.superclass.has_method?("before")) %}
          {% to_insert = "#{yield}\nsuper" %}
          {{ (existing.body.stringify.gsub(/super/, to_insert)).id }}
        {% else %}
          previous_def
          {{ (yield).id }}
        {% end %}
      {% else %}
        {% if @type.superclass && (@type.superclass.has_method?(:before)) %}
          ({{ (yield).id }})
          super
        {% else %}
          {{ (yield).id }}
        {% end %}
      {% end %}


end
